import React, {Fragment, useEffect, useState} from "react";
import { useHistory, Link } from "react-router-dom";
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import axios from "axios";
import EditPlant from "./EditPlant";
import Button from '@material-ui/core/Button';

const useStyles = makeStyles({
  table: {
    minWidth: 650,
  },
});

const ListPlants = () => {
  const classes = useStyles();
  const [plants, setPlants] = useState([])


  const getPlants = async () => {
    return await axios("http://localhost:5000/plants")
      .then((res) => {
        setPlants(res.data)
      })
      .catch((e) => {
        console.log("error listing plants", e);
      })
  }

  const deletePlant = async (id) => {
    return await axios(`http://localhost:5000/plants/${id}`, {
      method: "DELETE"
    })
      .then((plantDeleted) => {
        setPlants(plants.filter(plant => plant.plant_id !== id))
      })
      .catch((e) => {
        console.log("error listing plants", e);
      })
  }

  const history = useHistory();



  useEffect(() => {
    getPlants()
  })


  return (
    <Fragment>
      <h1>Plant Catalogue</h1>
      <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>ID</TableCell>
            <TableCell align="right">Name</TableCell>
            <TableCell align="right">Description</TableCell>
            <TableCell align="right">Image URL</TableCell>
            <TableCell align="Center">Edit Plants</TableCell>
            <TableCell align="Center">Delete Plant</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {plants.map((plant) => (
            <TableRow key={plant.plant_id}>
              <TableCell component="th" scope="row">
                {plant.plant_id}
              </TableCell>
              <TableCell align="right">{plant.name}</TableCell>
              <TableCell align="right">{plant.description}</TableCell>
              <TableCell align="right"><a href="https://www.w3schools.com/">{plant.image}</a></TableCell>
              <TableCell align="center"><EditPlant plant={plant}/></TableCell>
              <TableCell align="center"><Button onClick={()=> deletePlant(plant.plant_id)} variant="contained" color="secondary">
              Delete
      </Button></TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
    </Fragment>

  );
}


export default ListPlants;
